$(function(){var dateToday = new Date(); dateToday.setDate(dateToday.getDate()+2); 
	var nextDay = dateToday.getDate()+"-"+(dateToday.getMonth()+1)+"-"+dateToday.getFullYear();
$(".datepicker").val(nextDay);
 $(".datepicker").datepicker({dateFormat:"dd-mm-yy",minDate: dateToday,beforeShow:function(e,i){$("#ui-datepicker-div").addClass("ui-datepicker--calendar")},onClose:function(e,i){$("#ui-datepicker-div").css("display","none"),$("#ui-datepicker-div").removeClass("ui-datepicker--calendar")}})});